import React, { useEffect, useState } from 'react';
import { useLocalSearchParams } from 'expo-router';
import { Image, ScrollView, Text, View, Pressable, TextInput } from 'react-native';
import api from '../../src/api/client';
import { showToast } from '../../src/ui/toast';
import { useCart } from '../../src/state/useCart';
import { useWishlist } from '../../src/state/useWishlist';

type Product = {
  _id: string;
  title: string;
  description: string;
  price: number;
  images: string[];
  sizes?: string[];
  colors?: string[];
  rating?: number;
};

export default function ProductDetail() {
  const { id } = useLocalSearchParams<{ id: string }>();
  const [product, setProduct] = useState<Product | null>(null);
  const [size, setSize] = useState<string>('');
  const [review, setReview] = useState('');
  const [rating, setRating] = useState(5);
  const { add: addToCart } = useCart();
  const { items: wished, add: wishAdd, remove: wishRemove } = useWishlist();

  useEffect(() => {
    if (!id) return;
    let mounted = true;
    api.get(`/api/products/${id}`).then(r => {
      if (mounted) setProduct(r.data);
    });
    return () => {
      mounted = false;
    };
  }, [id]);

  if (!product) {
    return (
      <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
        <Text>Loading...</Text>
      </View>
    );
  }

  return (
    <ScrollView contentContainerStyle={{ padding: 16 }}>
      {product.images?.[0] && (
        <Image source={{ uri: product.images[0] }} style={{ width: '100%', aspectRatio: 3/4, borderRadius: 12, marginBottom: 16 }} />
      )}
      <View style={{ flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center' }}>
        <Text style={{ fontSize: 20, fontWeight: '700', marginBottom: 8 }}>{product.title}</Text>
        {(() => { const isWished = !!wished.find((w) => w._id === product._id); return (
          <Pressable onPress={() => (isWished ? wishRemove(product._id) : wishAdd({_id:product._id,title:product.title,price:product.price,images:product.images}))}>
            <Text style={{ fontSize: 20, color: isWished ? '#ff3f6c' : '#999' }}>{isWished ? '' : ''}</Text>
          </Pressable>
        ); })()}
      </View>
      <Text style={{ fontSize: 14, color: '#666', marginBottom: 6 }}>{product.rating || 0} </Text>
      {(() => { const original = Math.round(product.price * 1.25); const discount = Math.max(0, Math.round(((original - product.price)/original)*100)); return (
        <View style={{ flexDirection: 'row', alignItems: 'center', marginBottom: 12 }}>
          <Text style={{ fontSize: 18, color: '#26a541', fontWeight: '700' }}>{product.price}</Text>
          <Text style={{ marginLeft: 8, color: '#999', textDecorationLine: 'line-through' }}>{original}</Text>
          <Text style={{ marginLeft: 8, color: '#26a541' }}>{discount}% OFF</Text>
        </View>
      ); })()}
      <Text style={{ color: '#444', marginBottom: 16 }}>{product.description}</Text>

      {/* Size selector */}
      <View style={{ marginBottom: 12 }}>
        <Text style={{ fontWeight: '600', marginBottom: 8 }}>Select Size</Text>
        <View style={{ flexDirection: 'row', flexWrap: 'wrap', gap: 8 }}>
          {(product.sizes || ['S','M','L','XL','XXL']).map((s) => (
            <Pressable key={s} onPress={() => setSize(s)} style={{ paddingHorizontal: 12, height: 36, borderWidth: 1, borderColor: size === s ? '#ff3f6c' : '#ddd', borderRadius: 18, alignItems: 'center', justifyContent: 'center' }}>
              <Text style={{ color: size === s ? '#ff3f6c' : '#111' }}>{s}</Text>
            </Pressable>
          ))}
        </View>
      </View>

      <View style={{ flexDirection: 'row', gap: 10, marginBottom: 16 }}>
        <Pressable onPress={() => { addToCart({ _id: product._id, title: product.title, price: product.price, images: product.images }, 1); showToast('Added to cart'); }} style={{ flex: 1, backgroundColor: '#ff3f6c', padding: 14, borderRadius: 8, alignItems: 'center' }}>
          <Text style={{ color: 'white', fontWeight: '600' }}>Add to cart</Text>
        </Pressable>
      </View>

      {/* Reviews */}
      <View style={{ marginTop: 8 }}>
        <Text style={{ fontWeight: '700', marginBottom: 8 }}>Reviews</Text>
        <View style={{ gap: 8, marginBottom: 12 }}>
          <TextInput placeholder="Write your review" value={review} onChangeText={setReview} style={{ borderWidth: 1, borderColor: '#ddd', borderRadius: 8, padding: 10 }} />
          <ScrollView horizontal showsHorizontalScrollIndicator={false}>
            {[1,2,3,4,5].map((r) => (
              <Pressable key={r} onPress={() => setRating(r)} style={{ paddingHorizontal: 10, height: 32, borderWidth: 1, borderColor: rating === r ? '#ff3f6c' : '#ddd', borderRadius: 16, alignItems: 'center', justifyContent: 'center', marginRight: 8 }}>
                <Text style={{ color: rating === r ? '#ff3f6c' : '#111' }}>{r} </Text>
              </Pressable>
            ))}
          </ScrollView>
          <Pressable onPress={async () => { try { await api.post(`/api/products/${product._id}/reviews`, { rating, comment: review }); setReview(''); } catch (e: any) { /* if unauthorized, inform user */ } }} style={{ backgroundColor: '#111', padding: 12, borderRadius: 8, alignItems: 'center' }}>
            <Text style={{ color: 'white' }}>Submit review</Text>
          </Pressable>
        </View>
      </View>
    </ScrollView>
  );
}

