  function inc(id: string) {
    const next = items.map((i) => (i.productId === id ? { ...i, quantity: i.quantity + 1 } : i));
    setAndSave(next);
  }

  function dec(id: string) {
    const next = items
      .map((i) => (i.productId === id ? { ...i, quantity: Math.max(1, i.quantity - 1) } : i))
      .filter((i) => i.quantity > 0);
    setAndSave(next);
