'use client';

import { useState, useEffect } from 'react';
import { signIn } from 'next-auth/react';
import Link from 'next/link';

export default function LoginPage() {
        const [mode, setMode] = useState<'password' | 'otp'>('password');
        const [email, setEmail] = useState('');
        const [password, setPassword] = useState('');
        const [phone, setPhone] = useState('');
        const [otp, setOtp] = useState('');
        const [otpSent, setOtpSent] = useState(false);
        const [loading, setLoading] = useState(false);
        const [error, setError] = useState('');
        const [resendCooldown, setResendCooldown] = useState(0);

        useEffect(() => {
                if (resendCooldown > 0) {
                        const timer = setTimeout(() => setResendCooldown(resendCooldown - 1), 1000);
                        return () => clearTimeout(timer);
                }
        }, [resendCooldown]);

        async function handlePasswordLogin(e: React.FormEvent) {
                e.preventDefault();
                setLoading(true);
                setError('');
                try {
                        const res = await fetch('/api/auth/login', {
                                method: 'POST',
                                headers: { 'Content-Type': 'application/json' },
                                body: JSON.stringify({ email, password }),
                        });

                        if (!res.ok) {
                                const data = await res.json();
                                throw new Error(data.message || 'Login failed');
                        }

                        window.location.href = '/';
                } catch (e: any) {
                        setError(e.message || 'Login failed');
                } finally {
                        setLoading(false);
                }
        }

        async function handleSendOTP(e?: React.FormEvent) {
                if (e) e.preventDefault();
                setLoading(true);
                setError('');
                try {
                        const res = await fetch('/api/auth/send-otp', {
                                method: 'POST',
                                headers: { 'Content-Type': 'application/json' },
                                body: JSON.stringify({ phone }),
                        });

                        if (!res.ok) throw new Error((await res.json()).message);

                        setOtpSent(true);
                        setResendCooldown(30);
                } catch (e: any) {
                        setError(e.message || 'Failed to send OTP');
                } finally {
                        setLoading(false);
                }
        }

        async function handleVerifyOTP(e: React.FormEvent) {
                e.preventDefault();
                setLoading(true);
                setError('');
                try {
                        const res = await fetch('/api/auth/verify-otp', {
                                method: 'POST',
                                headers: { 'Content-Type': 'application/json' },
                                body: JSON.stringify({ phone, otp }),
                        });

                        if (!res.ok) throw new Error((await res.json()).message);

                        window.location.href = '/';
                } catch (e: any) {
                        setError(e.message || 'Invalid OTP');
                } finally {
                        setLoading(false);
                }
        }

        async function handleGoogleLogin() {
                setLoading(true);
                await signIn('google', { callbackUrl: '/api/auth/callback/google' });
        }

        return (
                <div className="max-w-md mx-auto">
                        <h1 className="text-3xl font-bold mb-6" style={{ color: '#0B7FB3' }}>Welcome Back</h1>

                        <div className="flex gap-2 mb-6">
                                <button
                                        className={`flex-1 py-2 rounded-lg transition-colors ${
                                                mode === 'password'
                                                        ? 'bg-[#0B7FB3] text-white'
                                                        : 'bg-neutral-800 text-white/70 hover:text-white'
                                        }`}
                                        onClick={() => {
                                                setMode('password');
                                                setError('');
                                        }}
                                >
                                        Password
                                </button>
                                <button
                                        className={`flex-1 py-2 rounded-lg transition-colors ${
                                                mode === 'otp'
                                                        ? 'bg-[#0B7FB3] text-white'
                                                        : 'bg-neutral-800 text-white/70 hover:text-white'
                                        }`}
                                        onClick={() => {
                                                setMode('otp');
                                                setError('');
                                                setOtpSent(false);
                                        }}
                                >
                                        OTP
                                </button>
                        </div>

                        {mode === 'password' ? (
                                <form onSubmit={handlePasswordLogin} className="space-y-4">
                                        <div>
                                                <label className="block text-sm mb-1 text-white/80">Email</label>
                                                <input
                                                        className="w-full px-4 py-3 rounded-lg bg-neutral-900 border border-white/10 focus:border-[#0B7FB3] focus:outline-none transition-colors"
                                                        placeholder="Enter your email"
                                                        type="email"
                                                        value={email}
                                                        onChange={(e) => setEmail(e.target.value)}
                                                        required
                                                />
                                        </div>
                                        <div>
                                                <label className="block text-sm mb-1 text-white/80">Password</label>
                                                <input
                                                        className="w-full px-4 py-3 rounded-lg bg-neutral-900 border border-white/10 focus:border-[#0B7FB3] focus:outline-none transition-colors"
                                                        placeholder="Enter your password"
                                                        type="password"
                                                        value={password}
                                                        onChange={(e) => setPassword(e.target.value)}
                                                        required
                                                />
                                        </div>
                                        <div className="text-right">
                                                <Link
                                                        href="/forgot-password"
                                                        className="text-sm hover:underline"
                                                        style={{ color: '#0B7FB3' }}
                                                >
                                                        Forgot Password?
                                                </Link>
                                        </div>
                                        {error && <p className="text-red-400 text-sm">{error}</p>}
                                        <button
                                                className="w-full py-3 rounded-lg font-semibold transition-colors"
                                                style={{ backgroundColor: '#0B7FB3', color: 'white' }}
                                                disabled={loading}
                                        >
                                                {loading ? 'Logging in...' : 'Login'}
                                        </button>
                                </form>
                        ) : (
                                <div className="space-y-4">
                                        {!otpSent ? (
                                                <form onSubmit={handleSendOTP} className="space-y-4">
                                                        <div>
                                                                <label className="block text-sm mb-1 text-white/80">Phone Number</label>
                                                                <input
                                                                        className="w-full px-4 py-3 rounded-lg bg-neutral-900 border border-white/10 focus:border-[#0B7FB3] focus:outline-none transition-colors"
                                                                        placeholder="+1234567890"
                                                                        type="tel"
                                                                        value={phone}
                                                                        onChange={(e) => setPhone(e.target.value)}
                                                                        required
                                                                />
                                                        </div>
                                                        {error && <p className="text-red-400 text-sm">{error}</p>}
                                                        <button
                                                                className="w-full py-3 rounded-lg font-semibold transition-colors"
                                                                style={{ backgroundColor: '#0B7FB3', color: 'white' }}
                                                                disabled={loading}
                                                        >
                                                                {loading ? 'Sending...' : 'Send OTP'}
                                                        </button>
                                                </form>
                                        ) : (
                                                <form onSubmit={handleVerifyOTP} className="space-y-4">
                                                        <div>
                                                                <label className="block text-sm mb-1 text-white/80">Enter OTP</label>
                                                                <input
                                                                        className="w-full px-4 py-3 rounded-lg bg-neutral-900 border border-white/10 focus:border-[#0B7FB3] focus:outline-none transition-colors text-center text-2xl tracking-widest"
                                                                        placeholder="000000"
                                                                        type="text"
                                                                        maxLength={6}
                                                                        value={otp}
                                                                        onChange={(e) => setOtp(e.target.value.replace(/\D/g, ''))}
                                                                        required
                                                                />
                                                        </div>
                                                        {error && <p className="text-red-400 text-sm">{error}</p>}
                                                        <button
                                                                className="w-full py-3 rounded-lg font-semibold transition-colors"
                                                                style={{ backgroundColor: '#0B7FB3', color: 'white' }}
                                                                disabled={loading}
                                                        >
                                                                {loading ? 'Verifying...' : 'Verify OTP'}
                                                        </button>
                                                        <div className="flex gap-2">
                                                                <button
                                                                        type="button"
                                                                        onClick={() => {
                                                                                setOtpSent(false);
                                                                                setOtp('');
                                                                                setError('');
                                                                                setResendCooldown(0);
                                                                        }}
                                                                        className="flex-1 text-sm text-white/70 hover:text-white"
                                                                >
                                                                        Change Number
                                                                </button>
                                                                <button
                                                                        type="button"
                                                                        onClick={() => handleSendOTP()}
                                                                        disabled={resendCooldown > 0 || loading}
                                                                        className="flex-1 text-sm hover:underline disabled:opacity-50 disabled:cursor-not-allowed"
                                                                        style={{ color: resendCooldown > 0 ? '#9ca3af' : '#0B7FB3' }}
                                                                >
                                                                        {resendCooldown > 0 ? `Resend in ${resendCooldown}s` : 'Resend OTP'}
                                                                </button>
                                                        </div>
                                                </form>
                                        )}
                                </div>
                        )}

                        <div className="relative my-6">
                                <div className="absolute inset-0 flex items-center">
                                        <div className="w-full border-t border-white/10"></div>
                                </div>
                                <div className="relative flex justify-center text-sm">
                                        <span className="px-2 bg-black text-white/60">Or continue with</span>
                                </div>
                        </div>

                        <button
                                onClick={handleGoogleLogin}
                                disabled={loading}
                                className="w-full py-3 px-4 rounded-lg bg-white text-black font-semibold hover:bg-gray-100 transition-colors flex items-center justify-center gap-3"
                        >
                                <svg className="w-5 h-5" viewBox="0 0 24 24">
                                        <path
                                                fill="currentColor"
                                                d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"
                                        />
                                        <path
                                                fill="currentColor"
                                                d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z"
                                        />
                                        <path
                                                fill="currentColor"
                                                d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z"
                                        />
                                        <path
                                                fill="currentColor"
                                                d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z"
                                        />
                                </svg>
                                Continue with Google
                        </button>

                        <p className="mt-6 text-center text-sm text-white/70">
                                New to Myntra?{' '}
                                <Link href="/signup" className="font-semibold hover:underline" style={{ color: '#0B7FB3' }}>
                                        Create an account
                                </Link>
                        </p>
                </div>
        );
}

