        {products.length === 0 ? (
          <div className="flex flex-col items-center justify-center py-16">
            <div className="relative w-64 h-64 mb-6">
              <Image
                src="https://images.unsplash.com/photo-1483985988355-763728e1935b?q=80&w=800&auto=format&fit=crop"
