import { NextRequest, NextResponse } from 'next/server';
import { connectToDatabase } from '@/lib/mongodb';
import { razorpay } from '@/lib/razorpay';
import { Order } from '@/models/Order';
import { Product } from '@/models/Product';

function generateOrderNumber(): string {
	return `ORD-${Date.now()}-${Math.floor(Math.random() * 1000).toString().padStart(3, '0')}`;
}

export async function POST(req: NextRequest) {
	try {
		await connectToDatabase();
		const body = await req.json();
		const items = Array.isArray(body.items) ? body.items : [];
		const totalAmount = Number(body.totalAmount);
		const userId = body.userId as string;
		if (!items.length || !totalAmount || !userId) {
			return NextResponse.json({ message: 'Invalid payload' }, { status: 400 });
		}
		const productIds = items.map((i: any) => i.product);
		const products = await Product.find({ _id: { $in: productIds } });
		const itemsWithSnapshot = items.map((it: any) => {
			const p = products.find((pp: any) => pp._id.toString() === it.product);
			return {
				product: it.product,
				title: p?.title || 'Product',
				image: p?.images?.[0] || '',
				quantity: Number(it.quantity) || 1,
				price: Number(it.price) || Number(p?.price) || 0,
			};
		});

		const order = await Order.create({
			orderNumber: generateOrderNumber(),
			user: userId,
			items: itemsWithSnapshot,
			totalAmount,
			status: 'created',
		});
		const razorOrder = await razorpay.orders.create({
			amount: Math.round(totalAmount * 100),
			currency: 'INR',
			receipt: order._id.toString(),
		});
		order.razorpayOrderId = razorOrder.id as any;
		await order.save();
		return NextResponse.json({ razorOrderId: razorOrder.id, orderId: order._id, orderNumber: order.orderNumber });
	} catch (e: any) {
		return NextResponse.json({ message: e?.message || 'Server error' }, { status: 500 });
	}
}

import { NextRequest, NextResponse } from 'next/server';
import crypto from 'crypto';
import { connectToDatabase } from '@/lib/mongodb';
import { Order } from '@/models/Order';

const RAZORPAY_KEY_SECRET = process.env.RAZORPAY_KEY_SECRET as string;

export async function POST(req: NextRequest) {
        await connectToDatabase();
        const { razorpay_order_id, razorpay_payment_id, razorpay_signature } = await req.json();
        if (!razorpay_order_id || !razorpay_payment_id || !razorpay_signature)
                return NextResponse.json({ message: 'Invalid payload' }, { status: 400 });

        const order = await Order.findOne({ razorpayOrderId: razorpay_order_id });
        if (!order) return NextResponse.json({ message: 'Order not found' }, { status: 404 });

        const body = `${razorpay_order_id}|${razorpay_payment_id}`;
        const expectedSignature = crypto
                .createHmac('sha256', RAZORPAY_KEY_SECRET)
                .update(body)
                .digest('hex');

        if (expectedSignature !== razorpay_signature) {
                order.status = 'failed';
                await order.save();
                return NextResponse.json({ message: 'Invalid signature' }, { status: 400 });
        }

        order.status = 'processing';
        order.razorpayPaymentId = razorpay_payment_id;
        order.razorpaySignature = razorpay_signature;

        // Set delivery date to 7 days from now
        const deliveryDate = new Date();
        deliveryDate.setDate(deliveryDate.getDate() + 7);
        order.deliveryDate = deliveryDate;

        // Initialize tracking status
        order.trackingStatus = [
                {
                        stage: 'processing',
                        message: 'Order confirmed and being processed',
                        updatedAt: new Date(),
                }
        ];

        await order.save();

        return NextResponse.json({ message: 'Payment verified', orderId: order._id });
}

