'use client';

import { useState, useEffect } from 'react';
import { useRouter } from 'next/navigation';

interface Address {
  _id?: string;
  line1: string;
  line2?: string;
  city: string;
  state: string;
  zip: string;
  phone?: string;
}

interface PaymentMethod {
  _id?: string;
  type: 'card' | 'upi' | 'netbanking';
  last4: string;
  brand?: string;
  expiryMonth?: number;
  expiryYear?: number;
  upiId?: string;
}

interface Order {
  _id: string;
  orderNumber: string;
  items: any[];
  totalAmount: number;
  status: string;
  createdAt: string;
}

interface User {
  _id: string;
  name: string;
  email: string;
  phone?: string;
  addresses: Address[];
  paymentMethods: PaymentMethod[];
}

export default function DashboardPage() {
  const router = useRouter();
  const [user, setUser] = useState<User | null>(null);
  const [orders, setOrders] = useState<Order[]>([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('profile');
  const [orderFilter, setOrderFilter] = useState('all');

  const [editingProfile, setEditingProfile] = useState(false);
  const [profileData, setProfileData] = useState({ name: '', email: '', phone: '' });

  const [showAddressModal, setShowAddressModal] = useState(false);
  const [editingAddress, setEditingAddress] = useState<Address | null>(null);
  const [addressForm, setAddressForm] = useState<Address>({ line1: '', city: '', state: '', zip: '' });

  const [showPaymentModal, setShowPaymentModal] = useState(false);
  const [paymentForm, setPaymentForm] = useState<PaymentMethod>({ type: 'card', last4: '', brand: '' });

  const [showCancelModal, setShowCancelModal] = useState(false);
  const [cancelOrderId, setCancelOrderId] = useState('');
  const [cancelReason, setCancelReason] = useState('');

  const [showTrackModal, setShowTrackModal] = useState(false);
  const [trackingData, setTrackingData] = useState<any>(null);

  useEffect(() => {
    fetchUserData();
    fetchOrders();
  }, []);

  async function fetchUserData() {
    try {
      const res = await fetch('/api/user/me');
      if (!res.ok) {
        router.push('/login');
        return;
      }
      const data = await res.json();
      setUser(data);
      setProfileData({ name: data.name, email: data.email, phone: data.phone || '' });
    } catch (error) {
      router.push('/login');
    } finally {
      setLoading(false);
    }
  }

  async function fetchOrders() {
    try {
      const res = await fetch('/api/orders');
      if (res.ok) {
        const data = await res.json();
        setOrders(data);
      }
    } catch (error) {
      console.error('Failed to fetch orders');
    }
  }

  async function updateProfile() {
    try {
      const res = await fetch('/api/user/update', {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(profileData),
      });
      if (res.ok) {
        const updated = await res.json();
        setUser(updated);
        setEditingProfile(false);
        alert('Profile updated successfully!');
      }
    } catch (error) {
      alert('Failed to update profile');
    }
  }

  async function saveAddress() {
    try {
      const res = await fetch('/api/user/address', {
        method: editingAddress ? 'PUT' : 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(editingAddress ? { ...addressForm, _id: editingAddress._id } : addressForm),
      });
      if (res.ok) {
        await fetchUserData();
        setShowAddressModal(false);
        setEditingAddress(null);
        setAddressForm({ line1: '', city: '', state: '', zip: '' });
        alert(editingAddress ? 'Address updated!' : 'Address added!');
      }
    } catch (error) {
      alert('Failed to save address');
    }
  }

  async function deleteAddress(id: string) {
    if (!confirm('Delete this address?')) return;
    try {
      const res = await fetch(`/api/user/address?id=${id}`, { method: 'DELETE' });
      if (res.ok) {
        await fetchUserData();
        alert('Address deleted');
      }
    } catch (error) {
      alert('Failed to delete address');
    }
  }

  async function savePaymentMethod() {
    try {
      const res = await fetch('/api/user/payment', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(paymentForm),
      });
      if (res.ok) {
        await fetchUserData();
        setShowPaymentModal(false);
        setPaymentForm({ type: 'card', last4: '', brand: '' });
        alert('Payment method added!');
      }
    } catch (error) {
      alert('Failed to save payment method');
    }
  }

  async function deletePaymentMethod(id: string) {
    if (!confirm('Delete this payment method?')) return;
    try {
      const res = await fetch(`/api/user/payment?id=${id}`, { method: 'DELETE' });
      if (res.ok) {
        await fetchUserData();
        alert('Payment method deleted');
      }
    } catch (error) {
      alert('Failed to delete payment method');
    }
  }

  async function handleCancelOrder() {
    if (!cancelReason) {
      alert('Please select a cancellation reason');
      return;
    }
    try {
      const res = await fetch('/api/orders/cancel', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ orderId: cancelOrderId, reason: cancelReason }),
      });
      if (res.ok) {
        await fetchOrders();
        setShowCancelModal(false);
        setCancelOrderId('');
        setCancelReason('');
        alert('Order cancelled successfully');
      } else {
        alert('Failed to cancel order');
      }
    } catch (error) {
      alert('Failed to cancel order');
    }
  }

  async function handleTrackOrder(orderId: string) {
    try {
      const res = await fetch(`/api/orders/track?orderId=${orderId}`);
      if (res.ok) {
        const data = await res.json();
        setTrackingData(data);
        setShowTrackModal(true);
      } else {
        alert('Failed to fetch tracking information');
      }
    } catch (error) {
      alert('Failed to fetch tracking information');
    }
  }

  if (loading) {
    return <div className="container py-20 text-center">Loading...</div>;
  }

  if (!user) {
    return null;
  }

  return (
    <div className="container py-8">
      <h1 className="text-3xl font-bold mb-6" style={{ color: '#0B7FB3' }}>My Dashboard</h1>

      <div className="grid md:grid-cols-4 gap-6">
        <aside className="md:col-span-1">
          <nav className="card p-4 space-y-2">
            <button
